    left: 0;
    height: 80px;
    padding: 0 10px;
    box-shadow: 0 0 40px 0 rgba(0, 0, 0, 0.1);
    width: 100%;
    backface-visibility: hidden;
    transform: translateY(0%);
    transition: all .4s ease;
    background: #000;
    color: #fff;
    z-index: 1000;
}

.ampize-header a {
    text-decoration: none;
}

.ampize-menu-tabs {
    margin-left: 60px
}

.ampize-menu-tab {
    padding: 6px 20px;
    color: #fff;
    font-weight: 400;
    margin: 0 4px;
    position: relative;
    transition: .1s ease color;
}

.ampize-menu-tab > a {
    display: block;
    margin: auto;
    z-index: 10;
    text-transform: uppercase;
}

.ampize-menu-tab.active, .ampize-menu-sidebar-item.active {
    color: #F3B229;
}

.ampize-menu-tab::before {
    bottom: 0;
    box-shadow: 0 0 40px 0 rgba(0, 0, 0, 0.1);
    content: '';
    left: -10px;
    opacity: 0;
    position: absolute;
    right: -10px;
    top: 0;
    transition: opacity .2s ease-out;
}

.ampize-menu-tab:hover::before {
    opacity: 1;
}

.ampize-menu-tab::after {
    background: #F3B229;
    bottom: 0;
    content: '';
    height: 3px;
    left: 20px;
    margin: auto;
    opacity: 0;
    position: absolute;
    right: 20px;
    transition: .1s ease opacity;
}

.ampize-menu-tab.active::after {
    opacity: 1;
}

.ampize-menu-tab.active:hover::after {
    opacity: 0;
}

.ampize-menu-tab .ampize-menu-tab-list {
    opacity: 0;
    pointer-events: none;
    position: absolute;
    left: -10px;
    top: 100%;
    transition: .2s ease-out;
}

.ampize-menu-tab:hover .ampize-menu-tab-list {
    opacity: 1;
    pointer-events: all;
    z-index: 0;
}

.ampize-menu-tab .ampize-menu-tab-list-shadow {
    bottom: -50px;
    left: -50px;
    overflow: hidden;
    position: absolute;
    right: -50px;
    top: 0;
}

.ampize-menu-tab .ampize-menu-tab-list-shadow::before {
    bottom: 50px;
    box-shadow: 0 0 40px 0 rgba(0, 0, 0, 0.1);
    content: '';
    left: 50px;
    position: absolute;
    right: 50px;
    top: 0;
}

.ampize-menu-tab-list > ul {
    margin: 0;
    padding: 0;
    list-style: none;
    min-width: 250px;
    position: relative;
    background: #fff;
}

.ampize-menu-tab-list > ul li {
    box-sizing: border-box;
    position: relative;
}

.ampize-menu-tab-list > ul li:hover {
    background: #f8f8f8;
}

.ampize-menu-tab-list > ul li a {
    color: #333;
    font-size: 16px;
    line-height: 26px;
    display: block;
    padding: 7.5px 30px;
}

.ampize-sidebar-trigger {
    vertical-align: middle;
    text-align: center;
    line-height: 60px;
    font-size: 2rem;
}

amp-sidebar {
    background: #fff;
    width: 100vw;
}

.ampize-menu-sidebar-layer {
    height: 100%;
    position: relative;
}

.ampize-menu-sidebar-close {
    font-size: 30px;
    padding: 17px 20px;
    color: rgb(110, 110, 110);
    text-align: end;
}

.ampize-menu-sidebar-items {
    left: 0;
    overflow-x: hidden;
    overflow-y: scroll;
    position: absolute;
    right: 0;
    bottom: 80px;
    top: 60px;
}

.ampize-menu-sidebar-items .ampize-menu-sidebar-item:first-child {
    border-top: solid 1px #ededed;
}

.ampize-menu-sidebar-item {
    border-bottom: solid 1px #ededed;
    color: rgb(74, 74, 74);
    display: block;
    position: relative;
    text-transform: none;
    font-size: 25px;
    font-weight: 100;
    line-height: 20px;
    padding: 18px 35px 18px 25px;
}

.ampize-menu-sidebar-item-expand {
    position: absolute;
    right: 20px;
    font-size: 25px;
    color: rgb(110, 110, 110);
}

a.ampize-menu-sidebar-item {
    text-decoration: none;
}

.ampize-menu-sidebar-item>input[type="checkbox"] {
    position: absolute;
    visibility: hidden;
}

.ampize-menu-sidebar-item .ampize-menu-sidebar-submenu {
    background: #fff;
    bottom: 0;
    box-shadow: 0 3px 20px 0 rgba(0,0,0,0.075);
    left: 0;
    position: fixed;
    right: 0;
    top: 0;
    transform: translateX(100%);
    transition: transform 233ms cubic-bezier(0, 0, 0.21, 1);
    z-index: 2;
}

.ampize-menu-sidebar-item input:checked~.ampize-menu-sidebar-submenu {
    transform: translateX(0);
}

.ampize-menu-sidebar-submenu-return {
    color: #b3b3b3;
    font-weight: 300;
    left: 25px;
    position: absolute;
    top: 22px;
    letter-spacing: .56px;
    line-height: 16px;
    text-transform: uppercase;
    font-size: 20px;
}

.ampize-menu-sidebar-submenu-items {
    left: 0;
    overflow-x: hidden;
    overflow-y: scroll;
    position: absolute;
    right: 0;
    bottom: 0;
    top: 60px;
    color: rgb(74, 74, 74);
}

.ampize-menu-sidebar-submenu-item {
    font-size: 25px;
    font-weight: 300;
    line-height: 26px;
    padding: 15px 35px 15px 25px;
}

.ampize-menu-sidebar-submenu-item.ampize-menu-sidebar-item:first-child {
    border-top: none;
}

.ampize-btn {
    font-family: inherit;
    font-weight: inherit;
    font-size: 1rem;
    line-height: 1.125rem;
    padding: .7em .8em;
    text-decoration: none;
    white-space: nowrap;
    word-wrap: normal;
    vertical-align: middle;
    cursor: pointer;
    background-color: #000;
    color: #fff;
    border: 1px solid #fff;
}

.ampize-btn > a {
    text-decoration: none;
}

.ampize-btn-secondary {
    background-color: #fff;
    color: #000;
    border: 1px solid #000;
}

.ampize-header ~ #content {
    padding-top: 85px;
}

@media only screen and (max-width: 52rem){
    .ampize-header {
        height: 60px;
        top: -1px;
        left: -1px;
        width: calc(100% + 2px);
    }

    .ampize-nav-left {
        flex: 1;
    }

    .ampize-menu-logo {
        width: auto;
        flex: 1;
    }

    .ampize-header ~ #content {
        padding-top: 65px;
    }
}
